    pm.test("Indicates attempted access to an unreachable or non-existing resource like e.g. an unknown topic or partition. GET requests to endpoints not allowed in the accesslists will also result in this response.", function () {
      pm.expect(pm.response.status).to.be.eql("Not Found");});
}

if (pm.response.code == 413){
    pm.test("This implies the client is sending a request payload that is larger than the maximum message size the server can accept.", function () {
      pm.expect(pm.response.status).to.be.eql("Request Entity Too Large");});
}

if (pm.response.code == 415){
    pm.test("This implies the client is sending the request payload format in an unsupported format.", function () {
      pm.expect(pm.response.status).to.be.eql("Unsupported Media Type");});
}

if (pm.response.code == 422){
    pm.test("Indicates a bad request error. It could be caused by an unexpected request body format or other forms of request validation failure.", function () {
      pm.expect(pm.response.status).to.be.eql("Unprocessable Entity (WebDAV) (RFC 4918)");});
}

if (pm.response.code == 429){
    pm.test("Indicates that a rate limit threshold has been reached, and the client should retry again later.", function () {
      pm.expect(pm.response.status).to.be.eql("Too Many Requests");});
}

if (pm.response.code == 500){
    pm.test("A server-side problem that might not be addressable from the client side. Retriable Kafka errors will contain error code 50003 in the response body.", function () {
      pm.expect(pm.response.status).to.be.eql("Internal Server Error");});
}
